mapper.select:
	   select id, c_username, c_phone , n_age,
	   case when n_sex=1 then '男'
	   when n_sex=0 then '女' end as n_sex ,
	   from users
	   where n_deleted=0 
	    and c_username like CONCAT('%',#{user.c_username},'%')
   	    and c_phone like CONCAT('%',#{user.c_phone},'%')
   	   	and n_age = #{user.n_age}
   	     and n_sex = #{user.n_sex}
 	   	and substr(c_createDate,1,10) = #{user.c_createDate}
   	     and substr(c_updateDate,1,10) = #{user.c_updateDate}
   	   order by id limit #{page.index},#{page.rows}

	   select count(id)
	   from users
	   where n_deleted=0
	    and c_username like CONCAT('%',#{user.c_username},'%')
   	    and c_phone like CONCAT('%',#{user.c_phone},'%')
   	   	and n_age = #{user.n_age}
   	     and n_sex = #{user.n_sex}
 	   	and substr(c_createDate,1,10) = #{user.c_createDate}
   	     and substr(c_updateDate,1,10) = #{user.c_updateDate}
   	   order by id limit #{page.index},#{page.rows}

mapper.update:
UPDATE users SET
c_pwd = #{c_pwd},
c_phone = #{c_phone}, n_age = #{n_age}, n_sex = #{n_sex}, c_username = #{c_username}, c_updateDate = #{c_updateDate}, n_updater = #{n_updater} WHERE id = #{id}
